    /**
     * Generate a more truly "random" string.
     *
     *
     * @param  int $length     - length of string
     * @param int  $stringType - commutative of the class Constants for digits, lowercase and uppercase
     *
     * @return string
     */
    static public function random ( $length = 16, $stringType = 7, $firstCharNotZero = false ) {
        $string = '';

        if($firstCharNotZero){
            $string = rand(1, 9); // Sets the first character without the zero digit
        }
        while ( ( $len = strlen( $string ) ) < $length ) {
            $size = $length - $len;
            $stringPart = Str::random( $size );
            switch ( $stringType ) {
                case 1: //Only digits
                    $string .= preg_replace( '/[a-zA-Z]/', '', $stringPart );
                    break;
                case 2: //Only lowercase
                    $string .= preg_replace( '/[0-9A-Z]/', '', $stringPart );
                    break;
                case 3: //Only digits and lowecase
                    $string .= preg_replace( '/[A-Z]/', '', $stringPart );
                    break;
                case 4: //Only uppercase
                    $string .= preg_replace( '/[0-9a-z]/', '', $stringPart );
                    break;
                case 5: //Only digits and uppercase
                    $string .= preg_replace( '/[a-z]/', '', $stringPart );
                    break;
                case 6: //Only lowercase and uppercase
                    $string .= preg_replace( '/[0-9]/', '', $stringPart );
                    break;
                case 7: //All
                    $string .= $stringPart;
                    break;
                default: //other value
                    $string .= $stringPart;
                    break;
            }
        }

        return $string;
    }

    /**
     * Return a new key that does not exist in the table
     *
     * @param string $table    - the table name in the DB
     * @param int    $length   - length of key
     * @param int    $dataType - commutative of the class Constants for digits, lowercase and uppercase
     *
     * @return string
     *
     **/
    static public function getNewTableKey ( $table, $length = 10, $dataType = 7 ,$firstCharNotZero = false) {

        do {
            $key = Helper::random( $length, $dataType, $firstCharNotZero);
            $row = DB::table( $table )->select( 'id' )->where( 'key', $key )->first();
        } while ( $row != null );

        return $key;
    }

    /**
     * @param     $value
     * @param int $wordCount
     *
     * @return bool
     */
    static public function isStringOrBlank ( $value, $wordCount = 5 ) {

        $regExFormula = '/^([a-zA-Z_\p{Hebrew}]*[ ]?){1,%s}$/u';

        return preg_match( sprintf( $regExFormula, $wordCount ), $value ) != 0;
    }

    /**
     * @param     $value
     * @param int $wordCount
     *
     * @return bool
     */
    static public function isAlphaNumericOrBlank ( $value, $wordCount = 8 ) {

        $regExFormula = '/^([a-zA-Z0-9_\p{Hebrew}]*[ ]?){1,%s}$/u';

        return preg_match( sprintf( $regExFormula, $wordCount ), $value ) != 0;
    }

    /**
     * Check if the number is an Israeli phone.
     *
     * @param $value
     *
     * @return bool
     */
    static public function isIsraelPhone ( $value ) {

        return preg_match( '/^(0|972)(([57][0-9])|[23489]){1}([0-9]){7}$/', $value ) != 0;
    }
    static public function isValidEmail ( $value ) {

        return preg_match( '/^(([^<>()\[\]\\.,;:\s@"]+(\.[^<>()\[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/', $value ) != 0;
    }

    /**
     * Check if the number obey the E164 standard.
     *
     * @param $value
     *
     * @return bool
     */
    static public function isE164Phone ( $value ) {

        return preg_match( '/^\+?[1-9]\d{1,14}$/', $value ) != 0;
    }

    /**
     * Check if the number is an Israeli land phone( so called Bezeq phone).
     *
     * @param $value
     *
     * @return bool
     */
    static public function isIsraelLandPhone ( $value ) {

        if (preg_match( '/^\+?(972)?(\-)?0?[23489]{1}(\-)?[^0\D]{1}\d{6}$/', $value ) != 0) {
            return true;
        }

        return preg_match( '/^\+?(972)?(\-)?0?(7[2-9]){1}(\-)?[^0\D]{1}\d{6}$/', $value ) != 0;
    }

    static public function isIsraelMobilePhone ( $value ) {

        return preg_match( '/^\+?(972)?(\-)?0?(5[0-9]){1}(\-)?[^0\D]{1}\d{6}$/', $value ) != 0;
    }

    /**
     * For $yearRelated=true return the 'birth day with January 01'; for false
     * return the 'exact birth day'.
     * Example: today is 2017-03-30
     *          for $yearRelated=true we get 1987-01-01
     *          for $yearRelated=false we get 1987-03-30
     *
     * @param      $age
     * @param bool $yearRelated
     * @param bool $withNoTime
     *
     * @return string
     */
    static public function getDateFromAge ( &$age, $yearRelated = false, $withNoTime = true ) {

        $result = '';

        if ( false == is_numeric( $age ) || false == is_int( $age ) || 1 * $age < 1 ) {
            return $result;
        }

        $timeZone = new \DateTimeZone( config( 'constants.APP_DATETIME_ZONE' ) );

        if ( true == $withNoTime ) {
            $now = new \DateTime( 'today', $timeZone );
        } else {
            $now = new \DateTime( 'now', $timeZone );
        }

        if ( true == $yearRelated ) {
            $now = \DateTime::createFromFormat( config( 'constants.APP_DATETIME_DB_FORMAT' ), $now->format( 'Y' ) . '-01-01 00:00:00', $timeZone );
        }

        $now->sub( new \DateInterval( 'P' . $age . 'Y' ) );
        $result = $now->format( config( 'constants.APP_DATETIME_DB_FORMAT' ) );

        return $result;
    }

    static function yearFromAge($age) {
        return date("Y") - $age;
    }

    /**
     *
     * This functions gets a phone number (land or mobile)
     * without hyphens and adds a hyphen
     *
     * For example 0549836544 wiil change to 054-9836544
     *
     * @param int  $phoneNumber
     * @return string $newPhoneNumber
     */
    static function addHyphenToPhoneNumber( $phoneNumber ) {
        if ( strlen($phoneNumber) == 10 ) {
            $newPhoneNumber = substr($phoneNumber, 0, 3) . "-" . substr($phoneNumber, 3) ;
        } else if ( strlen($phoneNumber) == 9 ) {
            $newPhoneNumber = substr($phoneNumber, 0, 2) . "-" . substr($phoneNumber, 2) ;
        } else {
            return $phoneNumber;
        }

        return $newPhoneNumber;
    }

    static function sizeToBytes($size) {
        $realSize = 0;
        $size = strtolower($size);
        $sizeLength = strlen($size);
        if (substr($size, $sizeLength-2) === "mb") {
            $realSize = intval(substr($size, 0, -2));
            $realSize = $realSize * 1024 * 1024;
        }

        return $realSize;
    }

    static function isKosherPhone($phoneNumber) {
        // if($phoneNumber == '0523129999' || $phoneNumber == '0584671611' || $phoneNumber == '0587712000' || $phoneNumber == '0544593104'){ // Ivr test phone
        if($phoneNumber == '0523129999' ){ // Ivr test phone
            return true;
