        // Pelephone
        $koserPhones= self::KOSER_PREFIX_LIST;
        for($i=0; $i<10; $i++){
            $koserPhones[] = "0500$i";
        }
        foreach ($koserPhones as $prefix){
            if ( preg_match("/^(0" . $prefix . "[0-9]{5})$/",  $phoneNumber) ) {
                return true;
            }
        }
    
        return false;
    }

    /**
