    static function validateDate($date, $format = 'Y-m-d H:i:s')
    {
        $d = \DateTime::createFromFormat($format, $date);
        return $d && $d->format($format) == $date;
    }

    // returns the weighted randomly selected key
    static function randWeight($numbers) {
        $total = 0;
        foreach ($numbers as $number => $weight) {
            $total += $weight;
            $distribution[$number] = $total;
        }
        $rand = mt_rand(0, $total - 1);
        foreach ($distribution as $number => $weights) {
            if ($rand < $weights) { return $number; }
        }
    }
   public static function generateRandomString($length = 10) {
    $characters = '0123456789abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ';
    $charactersLength = strlen($characters);
    $randomString = '';
    for ($i = 0; $i < $length; $i++) {
        $randomString .= $characters[rand(0, $charactersLength - 1)];
    }
    return $randomString;
}
    public static function getEntityByKey($entityType, $entityKey){
        switch($entityType){
            case config('constants.GEOGRAPHIC_ENTITY_TYPE_BALLOT_BOX'): 
                $entity = BallotBox::select('id')->where('ballot_boxes.key', $entityKey)->first();
                break;
            case config('constants.GEOGRAPHIC_ENTITY_TYPE_CLUSTER'): 
                $entity = Cluster::select('id')->where('clusters.key', $entityKey)->first();
                break;
            default:
                $entity = null;
        }
        return $entity ? $entity->id : null;
    }

    //function get number and second number for calculate presents the function return round number
    //the function can get another params for check if the number present >0<1 return 1%
    public static function getPresent($all,$part,$roundZeroDotUp=false,$round=true,$numberRound=false){
        if($all==0)
        return 0;
        else {
            $present=($part*100)/$all;
            
            if($present>0 && $present<1 && $roundZeroDotUp )
                return 1;

            if($numberRound)    
            return $round?round((float)$present,$numberRound):$present;
            else
            return $round?round($present):$present;
        }
    }

    public static function makeHashCollection($arrCollection,$nameFieldKey){
        $hash=array();
        foreach ($arrCollection as $key => $item) {
            $valueKey=$item->$nameFieldKey;
            $hash[$valueKey]=$item;
        }
        return $hash;
    }

