    /**
     * function get string and remove all none numeric character
     *
     * @param string $string
     * @return string
     */
    public static function removeAllNoneNumericCharacters($string)
    {
        return preg_replace("/[^0-9]/", "", $string);
    }

    public static function removeCommaAndNewLineFromString($string)
    {
        try {
            $string = str_replace(',', ' ', $string);
            return trim(preg_replace('/\s\s+/', ' ', $string));
        } catch (\Throwable $th) {
            return $string;
        }
    }

    public static function mergeEloquentProperty($object1, $object2)
    {
        return (object) array_merge((array) $object1->attributesToArray(), (array) $object2->attributesToArray());
    }

    public static function transformerOrm($voter, array $arrayDetails)
    {
        $orm = [];
        foreach ($arrayDetails as $key => $field) {
            $orm[$field] = $voter->$field;
        }

        return  $orm;
    }
